class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt{};
        int ans{};
        for(int i = n-2; i >= 0; -- i){
            if(s[i] == '0') continue;
            if(s[i+1] == '0'){
                cnt++;

            }
            ans += cnt;
        }


        return ans;

    }
};
