class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,ans=0;
        for(int i=0;i<s.size();i++)
        {
             if(s[i]=='1')
             {
                 cnt++;
                 int j=i+1;
                 while(j<s.size()&&s[j]=='0')
                 j++;
                 if(j==i+1)
                 continue;
                 i=j-1;
                 ans+=cnt;
             }
        }
        return ans;
    }
};
