class Solution {
public:
    int maxOperations(string s) {
        vector<int> cnts;
        int n=s.length();
        for(int i=0;i<n;i++){
            int cnt = 0;
            while(i<n && s[i]=='1'){
                ++i;
                ++cnt;
            }
            if(cnt>0)
                cnts.push_back(cnt);
        }
        if(s[n-1]=='1'){
            cnts.pop_back();
        }
        cnts.resize((int)cnts.size());
        int prev=0;
        int ans = 0;
        for(int i=0;i<(int)(cnts.size());++i){
            cout << cnts[i] << " ";
            ans+=prev+cnts[i];
            prev+=cnts[i];
        }
        return ans;
    }
};
