class Solution {
public:
    int maxOperations(string s) {
        int ans = 0,count=0,check=0;
        for(auto i: s){
            if(i=='1'){
                count++;
                check=1;
            }
            else{
                if(check==1){
                    ans+=count;
                    check=0;
                }
            }
        }
        return ans;
    }
};
