class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        for (int i = 0, num = 0; i < s.length() - 1; i++) {
            if (s.charAt(i) == '1') {
                num++;
            }
            if (s.charAt(i) == '1' && s.charAt(i + 1) == '0') {
                ans += num;
            }



        }
        return ans;
    }
}
