class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        if(n == 1) return 0;
        vector<int> v;

        int cnt1 = 0;
        for(int i = 0 ; i < n; i++){
            cnt1 = 0;
            while(i < n && s[i] == '1'){
                cnt1 ++;
                i++;
            }
            if(i != n && cnt1 != 0){
                v.push_back(cnt1);
                i--;
            }
        }

        int ans = 0;

        for(int i = 0; i < v.size(); i++){
            ans += v[i]*(v.size() - i);
        }

        if(v.size() == 0) return 0;

        // ans += v[v.size()-1];
        return ans;
    }
};
