class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int i = 0;
        int n = s.size();
        int prev = 0;


        while( i< s.size()){
            if(s[i] == '0'){
                cnt += prev;
                while(i<n && s[i] == '0'){i++;}
            }
           else while(i<n && s[i] == '1'){i++; prev++;}

        }
        return cnt;
    }
};
