class Solution {
public:
    int maxOperations(string s) {
        bool ch = false;
        int op = 0;
        int count = 0;
        for(auto &x:s)
        {
            if(x=='1')
            {
                ch = true;
                count++;
            }
            else
            {
                if(ch)
                {
                    op+=count;
                }
                ch = false;
            }
        }
        return op;
    }
};
