class Solution {
public:
    int maxOperations(string s) {
        int t = 0;
        char curr = '0';
        int ret= 0;
        for(int i = 0; i < s.length(); i++) {
            char c = s[i];
            if (curr == '1') {
                switch (c) {
                    case '1':
                    t++;
                    break;
                    case '0':
                        curr = '0';
                        t++;
                        //cout<<t<<endl;
                        ret += t;
                    break;
                    default:
                }
                continue;
            } else {
                switch (c) {
                    case '1':
                        curr = '1';
                    break;
                    case '0':
                        continue;
                    break;
                    default:
                }
            }
        }
        if (curr == '0') {
            //ret += t;
                        //cout<<t<<endl;
        }
        return ret;
    }
};
