#define ll long long 
class Solution {
public:
    int maxOperations(string s) {
        ll num = 0;
        ll count = 0;
        ll n = s.size();
        for(ll i=n-1;i>=0;i--){
            if((i==n-1 && s[i]=='0') || (i!=n-1 && s[i]=='0' && s[i+1]=='1')){
                num++;
            }else if(s[i]=='1'){
                count+=num;
            }
        }
        return count;
    }
};
