class Solution:
    def maxOperations(self, s: str) -> int:
        counter = 0
        res = 0
        i = 0
        prev = 0
        while i < len(s):
            if s[i] == '1':
                prev = i
                while i + 1 < len(s) and s[i + 1] == '0': i += 1
                if i > prev: res += (1 + counter)
                counter += 1
            i += 1
        return res
