class Solution {
public:
    string compress(string s){
        string ans;
        for(auto &ch: s){
            if(ans.size()==0)ans.push_back(ch);
            else if(ans.back()==ch && ch=='0')continue;
            else ans.push_back(ch);
        }
        return ans;
    }
    int maxOperations(string s) {
        s= compress(s);
        int n= s.size();
        if(n<=1)return 0;
        vector<int>suff(n, 0);
        for(int i=0; i<n; i++)suff[i]= (s[i]=='0');
        for(int i=n-2; i>=0; i--)suff[i]+= suff[i+1];
        int ans=0;
        for(int i=0; i<n-1; i++){
            if(s[i]=='1')
                ans+= suff[i+1]; 
        }
        return ans;
    }
};
