class Solution {
public:
    int maxOperations(string s) {
       int ans = 0;
       int prev = 0;
       for (int i = 0;i<s.length()-1;i++){
        if (s[i] == '1' && s[i+1] == '0'){
            prev++;
            ans+=prev;
        }
        else if (s[i] == '1'){
            prev++;
        }
        // co 
       }
       return ans;
    }
};
