class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int x = 0;
        bool flag = true;
        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '1') {
                x++;
                flag = true;
            } else {
                if (flag) {
                    ans += x;
                }
                flag = false;
            }
        }
        return ans;
    }
};
