class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int count = 0;

        for(int i = 0;i<s.size();i++){
            if(s[i] == '1'){
               count++;
            }
            else{
                 for(int j = i +1 ;j<s.size();j++){
                     if(s[j] != '0'){
                         break;
                     }
                     else{
                         i = j;
                        }
                 }
               ans += count;
            }
        }
      return ans;
    }
};
