class Solution {
public:
    int maxOperations(string s) {
        int onecount = 0;
        int n = s.size();
        int ans = 0;

        for(int i=0; i<n-1; i++){
            if(s[i] == '1') onecount++;
            if(s[i]=='1' && s[i+1]=='0') ans += onecount;
        }
        return ans;
    }
};
