class Solution:
    def maxOperations(self, s: str) -> int:
        index = 0

        one_count = 0

        ans = 0

        while index < len(s):
            if s[index] == '1':
                one_count += 1
                index += 1
            else:
                while index < len(s) and s[index] == '0':
                    index += 1

                ans += one_count

        return ans
