class Solution {
    public int maxOperations(String s) {
       int ans = 0;
        int op = 0;
        boolean c = true;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                op++;
                c = true;
            } else {
                if (c) {
                    ans += op;
                }
                c = false;
            }
        }
        return ans;
    }
}
