class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), c = 0, ans = 0;
        for(int i = 0 ; i < n ; i ++ ){
            if(s[i] == '1') c++;

            if(i+1<n && s[i]=='1' && s[i+1] == '0') {
                ans += c;
            }
        }
        return ans;
    }
};
