class Solution {
    public int maxOperations(String s) {
        char[] arr = s.toCharArray();
        int n = arr.length, i = n - 2;
        List<Integer> list = new ArrayList<>();
        while(i >= 0){
            if(arr[i] == '1' && arr[i + 1] == '0'){
                int temp = 0;
                while(i >= 0 && arr[i] != '0'){
                    temp++;
                    i--;
                }
                list.add(temp);
            }
            i--;
        }
        int l = list.size(), count = 0;
        for(i = 0; i < l; i++){
            count += list.get(i) * (i + 1);
        }
        return count;
    }
}
