class Solution {
public:
    int maxOperations(string nums) {
        int n=nums.size();
         int prev=0;
         int annn=0,abcd=0;
         for(int pr=0;pr<n;pr++)
         {
            if(nums[pr]=='1')
            {
                int antrr=pr-prev;
                if(antrr>1)
                {
                    abcd+=annn;
                }
                annn++;
                prev=pr;
            }
         }
         if(nums[n-1]=='0')abcd+=annn;
         return abcd;
    }
};
