class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int zero=0,flag=0;
        int i=0;
        vector<int>v;
        while(s[i]=='0' && i<n) i++;
        for(int j=i;j<n;j++)
        {
            if(s[j]=='1') v.emplace_back(1);
            if(s[j]=='0' && v.back()!=0)
            {
                zero++;
                v.emplace_back(0);
            }
        }
        for(int i=0;i<v.size();i++)
        {
            if(v[i]==1) ans+=zero;
            else zero--;
        }
        return ans;

    }
};
