class Solution {
public:
    int maxOperations(string s) {
        unordered_map<char,int> mymap;
        int n = s.size();
        int ans = 0;
        for(int i = 0; i < n-1; i++){
            mymap[s[i]]++;
            if(s[i]=='0' && s[i+1]=='1') ans += mymap['1'];   
        }
        if(s[n-1] == '0') ans += mymap['1'];
        return ans;
    }
};
