using ll = long long;
class Solution {
public:
    ll maxOperations(string s) {
        reverse(s.begin(), s.end());
        int b = 0;
        ll ans = 0;
        int n = s.size();
        for(int i = 0, j = 0; i < n; i = j){
            while(j < n && s[i] == s[j]) j++;
            if(s[i] == '0') b++;
            else {
                ans += ll(j - i) * b;
            }
        }
        return ans;
    }
};
