impl Solution {
    pub fn max_operations(s: String) -> i32 {
        let mut res = 0;
        let mut on = 0;
        let mut chk = true;
        for c in s.chars() {
            match c {
                '1' => {
                    on += 1;
                    chk = true;
                }
                _ => {
                    if chk {
                        res += on;
                    }
                    chk = false;
                }
            }
        }
        res
    }
}
