typedef long long ll;

class Solution {
public:
    int maxOperations(string s) {
        int n = (int)s.size();
        int cnt1 = 0;
        int i = 0; 
        ll res = 0ll;
        while (i < n) {
            if (s[i] == '1') {
                cnt1++;
                i++;
                continue;
            }
            int j = i;
            while (j < n && s[j] == '0') {
                j++;
            }
            res += 1ll*cnt1;
            i = j;
        }
        return res;
    }
};
