class Solution {
public:
    int maxOperations(string s) {
        bool check=false;
        vector<int>v;
        int op=0,cnt=0;

        for(auto itr:s){
            if(itr=='0'&&check){
                v.push_back(0);
                check=false;
            }
            else if(itr=='1'){
                v.push_back(1);
                check=true;
            }
        }
        for(auto it:v){
            if(it==0){
                op+=cnt;
            }
            else{
                cnt+=1;
            }
        }
        return op;
    }
};
