class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int len = s.length();

        if (s[0] == '1') {
            ones++;
        }

        int ans = 0;

        for (int i = 1; i < len; i++) {
            if (s[i] == '0') {
                if (s[i - 1] == '1') {
                    ans += ones;
                }
            }
            else {
                ones++;
            }
        }

        return ans;
    }
};
