class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int cnt = 0, ans = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                if (i && s[i - 1] == '0')
                    ans += cnt;
                cnt++;
            }
        }
        return ans;
    }
};
