class Solution:
    def maxOperations(self, s: str) -> int:
        indices = []

        for idx in range(len(s)):
            if s[idx] == '1': indices.append(idx)

        if s[-1] == '0': indices.append(len(s))

        total_moves = 0
        for idx in range(len(indices) - 1):
            if indices[idx] + 1 != indices[idx + 1]:
                total_moves += idx + 1

        return total_moves
