class Solution {
    public int maxOperations(String s) {
        char[] arr = s.toCharArray();
        int len = arr.length;
        int oneCount = 0, i = 0, ans = 0;
        while (i < len){
            if (arr[i] == '1'){
                oneCount++;
                i++;
            } else {
                ans += oneCount;
                while (i < len && arr[i] == '0') i++;
            }
        }
        return ans;
    }
}
