class Solution {
    public int maxOperations(String s) {
        int moves = 0;
        int ones = 0;
        int lastZero = s.lastIndexOf('0');
        for (int i = 0; i <= lastZero; i++) {
            if (s.charAt(i) == '1') {
                ones++;
            } else if (s.charAt(i) == '0' && (i + 1 >= s.length() || s.charAt(i+1) == '1')) {
                moves += ones;
            }
        }
        return moves;
    }
}
