class Solution {
public:
    int maxOperations(string s) {
      int n=s.size();
      int ans=0,one=0,zero=0;
      for(int i=0;i<n;i++){
        if(s[i]=='1'){
            one++;
            zero=0;
        }
        else{
            if(!zero){
                ans+=one;
            }
            zero++;
        }
      }  
      return ans;
    }
};
