class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int cnt = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                cnt++;
                continue;
            }
            int j = i;
            while (j + 1 < s.length() && s.charAt(j + 1) == '0') {
                j++;
            }
            ans += cnt;
            i = j;
        }
        return ans;
    }
}
