class Solution {
public:
    int maxOperations(string s) {
        int c = 0;
        int ans = 0;
        int n = s.size();
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '0') {
                while (i >= 0 && s[i] == '0') i--;
                c++;
                if(i>=0 && s[i]=='1')
                ans += c;
            } else {
                ans += c;
            }
        }
        return ans;
    }
};
