class Solution {
public:
    int maxOperations(string s) {
        int cnt=count(s.begin(),s.end(),'1');
        int n=s.size();
        int ans=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0'){
                ans+=cnt;

                while(i>=0&&s[i]=='0'){
                    i--;
                }
                i++;


            }else if(s[i]=='1'){
                cnt--;
            }
        }
        return ans;
    }
};
