class Solution {
public:
    int maxOperations(string s) {
        int ans=0,c=1,x=0;
        for(char i:s){
            if(i=='1'){
                x++;
                c=1;
            }
            else{
                if(c)ans+=x;
                c=0;
            }
        }
        return ans;
    }
};
