class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int z = 0;
        boolean pre = true;
        char[] arr = s.toCharArray();
        for (int i = arr.length - 1; i > -1; i--) {
            if (arr[i] == '1') {
                ans += z;
                pre = true;
            } else if (pre) {
                z++;
                pre = false;
            }
        }
        return ans;
    }
}
