#define lli long long int

class Solution {
public:
    int maxOperations(string s) {
        lli n = s.length();
        lli ans = 0;
        lli cnt = 0;
        for(lli i=0; i<n; i++){
            if(s[i]=='1') cnt++;
            else if(i!=0 && s[i]=='0' && s[i-1]!='0') ans += cnt;
        }

        return ans;
    }
};
