class Solution {
public:
    int maxOperations(string s) {
        int n=s.length(),ans=0,cnt=0,k=0;
        int l=0,r=n-1;
        while(r>=0 && s[r]=='1')r--;
        while(l<=r){
            cnt=0;
            while(l<=r && s[l]=='1'){
                cnt++;
                l++;
            }
            if(cnt){
                k+=cnt;
                ans+=k;
            }
            else l++;
        }
        return ans;
    }
};
