class Solution {
public:
    int maxOperations(string s) {
        vector<int> blocks;
        int b = 0;
        int n = s.length();
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') b++;
            else if (b != 0) {
                blocks.push_back(b);
                b = 0;
            }
        }
        bool end = false;
        if (b != 0) {
            blocks.push_back(b);
            end = true;
        }
        int bSz = blocks.size();
        if (bSz == 0) return 0;
        int res = 0;
        for (int i = 1; i < bSz; i++) {
            // cout << blocks[i - 1] << " ";
            res += blocks[i - 1];
            blocks[i] += blocks[i - 1];
        }
        if (!end) res += blocks[bSz - 1];
        return res;
    }
};
