class Solution {
public:

    int maxOperations(string s) {
        int n=s.size();
        int ngrps=0;
        int ans=0;
        int count=0;
        unordered_map<int,int> m;
        int i=0;
        while(i<n)
        {
            bool flag=true;
            while(i<n&&s[i]=='1')
            {
                if(flag) ngrps++;
                flag=false;
                count++;
                i++;
            }
            m[ngrps]=count;
            i++;
        }

        if(s[n-1]=='1')
        {
            for(auto i:m)
            {
                cout<<i.first<<" "<<i.second<<" ";

                ans+=i.second;
                cout<<endl;
                cout<<ans<<" ";
                cout<<endl;
            }  
            ans-=m[ngrps];
        }
        else{
            for(auto i:m)
            {
                ans+=i.second;
            }  

        }
        return ans;
    }
};
