class Solution {
public:
    int maxOperations(string s) {
        int zerosGroup=0;
        int ans=0;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='1'){
                ans+=zerosGroup;
            }
            else{
                zerosGroup++;
                while(i>=0 && s[i]=='0'){
                    i--;
                }
                i++;
            }
        }
        return ans;
    }
};
