class Solution {
public:
    int maxOperations(string s) {
        int res = 0, p = 0, r = 0;
        bool k = 0;
        vector<int> h;
        for(int i = 0; i<s.size(); i++)
        {
            if(s[i] == '1')
            {
                h.push_back(1);
                k=1;
            }
            if(s[i] == '0' && k == 1)
            {
                h.push_back(0);
                k = 0;
            }
        }

        for(auto it : h)
        {
            if(it == 1)
            {
                p++;
            } else {
                res += p;
            }
        }

        return res;
    }
};
