class Solution:
    def maxOperations(self, s: str) -> int:
        groups = []
        for i in range(len(s)):
            if s[i] == "1":
                if i==0 or s[i-1] == "0":
                    groups.append(0)
                groups[-1] += 1
        if s[-1] == "0":
            groups.append(0)
        ans = 0
        for i in range(len(groups)):
            ans += (len(groups)-i-1) * groups[i]
        return ans
