class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cnt=0;
        bool f=1;
        for(auto i:s){
            if(i=='0'){
               if(f==1){
                    ans=ans+cnt;
                }else{
                    // ans+=0;
                    f=0;
               }
                f=0;
            }else{

                cnt++;
                f=1;
            }
        }
        return ans;
    }
};
