class Solution {
public:
    int maxOperations(string s) {
        vector<int>ones_num(s.size(),0);
        int ones=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                ones++;
            }
            ones_num[i]=ones;
        }
        int total=0;
        bool previous_zero=false;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='0' && !previous_zero){
                previous_zero=true;
                total+=ones_num[i];
            }
            if(s[i]=='1'){
                previous_zero=false;
            }
        }
        return total;
    }
};
