class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt = 0, res = 0;
        for(int i=0;i<n;i++)
        {
            if (s[i] == '1')
                cnt ++;
            else if (s[i] == '0' && cnt > 0)
            {
                res += cnt;
                while(i + 1 < n && s[i+1] == '0')
                    i++;
            }
        }
        return res;
    }
};
