class Solution {
    public int maxOperations(String s) {
        int co=0;
        int ans=0;
        boolean ones=false;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i)=='1'){
                co++;
                ones=true;
            }else if(s.charAt(i)=='0' && ones){
                ones=false;
                ans+=co;
            }
        }
        return ans;
    }
}
