class Solution {
    public int maxOperations(String s) {
        return sln1(s);
    }

    private int sln1(String s){
        int n = s.length();
        int idx = 0;
        while(idx<n && s.charAt(idx) != '1') idx++;
        int res =0; int total = 0;
        for( ;idx<n; idx++){
            int count1 = 1;
            while(idx+1 < n && s.charAt(idx+1)=='1') {
                idx++;
                count1++;
            }
            total+=count1;
            int count0 = 0;
            while(idx+1<n && s.charAt(idx+1) == '0') {
                count0++;
                idx++;
            }
            if(count0>0) res+=total;
        }
        return res;
    }
}
